import React, { useEffect, useState } from 'react';
import { getPosts } from '../api';
export const cohortName = '2110-VPI-WEB-PT';
// Use the APIURL variable for fetch requests
export const API = `https://strangers-things.herokuapp.com/api/${cohortName}`;
import {DeletePost, EditPost} from './DeletePost';


const Posts= (props) => {

  const [loadedPosts, setLoadedPosts] = useState([]);



    useEffect(() => {
    getPosts().then(result=>{
      setLoadedPosts(result.data.posts)
    }).catch(error=>{
      console.error(error)
    })}, []); 

    return (
        <div>
           <h1>Posts</h1>
          {loadedPosts.map(item => 
          <div key={item.author._id}>
          <p>{item.title}</p>
          <p>{item.author.username}</p>
          <p>{item.createdAt}</p>
          <p>{item.price}</p>
          <p>{item.description}</p>
          <p>{item.location}</p>
          <p>{item.willDeliver}</p>
          <DeletePost />
          {/* <EditPost /> */}
          <p>---------</p>
          </div>
          )}

        </div>


    )

}

export default Posts
