import { useState } from "react";
export const cohortName = '2110-VPI-WEB-PT';
export const API = `https://strangers-things.herokuapp.com/api/${cohortName}`;


// export const [authToken, setAuthToken] = useState("")

export async function getPosts() {
    try {
      const response = await fetch(`${API}/posts`);
      const result = await response.json();
      console.log(result)
      return result;
    } catch (error) {
      throw error;
    }
  }


  export async function registerNew (regInfo) {
    try{
      const response = await fetch(`${API}/users/register`,
      {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(
          regInfo
          ),
      }
      );
      const result = await response.json();
      console.log(result);
    } catch (error){
      console.error('could not register', error)
    }
  };

  export async function loginUser (loginInfo){
  fetch(`${API}/users/login`,{
     method: 'POST',
     headers: {
       'Content-Type': 'application/json'
     },
     body: JSON.stringify
     (
       loginInfo
       ),
   }
   ).then(response => response.json())
   .then(result =>{
     result.data.token ? localStorage.setItem("myToken", result.data.token): console.log(result);
   }).catch(console.error);
 }
