import { useEffect, useState } from "react"
import { callApi } from "../api"
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

const CreateMessage = (props) =>{
    const postId = props.postId
    const [message, setMessage] = useState("")
    const handleMessageSubmit = async (event) => {
event.preventDefault()
if(message === ""){
    document.getElementById("float").className="visible";
    document.getElementById("float").innerText = "Message Cannot Be Blank"
    setTimeout(function(){
        document.getElementById("float").className="hidden";
    },5000)
    setMessage("")
setClicked(false)
} else {
let messageInfo = {
    message: {
        content: `${message}`
    }
}
console.log(message)
console.log(postId)
const data = await callApi({ url:`/posts/${postId}/messages`, method: "post", token: localStorage.getItem("myToken"), body: messageInfo })
setMessage("")
setClicked(false)
if(data.success){
    document.getElementById("float").className="visible"
    document.getElementById("float").innerText = "Message Sent"
    setTimeout(function(){
        document.getElementById("float").className="hidden";
    },5000)
}
}
    }

const handleMessageChange = (event) =>{
    setMessage(event.target.value)
}
const [clicked, setClicked] = useState(false) 
const displayMessageForm = () =>{
    console.log("clicked")
    setClicked(true)


}


    return (
        clicked ?
        <Box component="form" noValidate autoComplete="off" onSubmit={handleMessageSubmit}>
            <div>
                <TextField required id="createmessage" value={message} onChange={handleMessageChange} label="Message:" defaultValue="Enter Message Here" />
                <button type="submit">Send Message</button>
            </div>
        </Box>
//         <div>
//     <form onSubmit={handleMessageSubmit}>
//         <label htmlFor="message">Message:</label>
//         <input type="text" name="message" value={message} onChange={handleMessageChange}/>
//         <button type="submit">Send Message</button>
//     </form>
// </div>
: 
        <button onClick={displayMessageForm}>Send Message</button>
    )
}


export default CreateMessage
