import { Link } from "react-router-dom"
import { useEffect, useState } from "react"
import { loginUser } from "../api"


const LoginForm = (props) =>{
    const [loginMessage, setLoginMessage] = useState({})
    const setCurrentAuthToken = props.setCurrentAuthToken
    const currentAuthToken = props.currentAuthToken
const authToken = props.authToken
const setAuthToken = props.setAuthToken 
    useEffect(() => {
        localStorage.getItem("myToken")
        ?
        setCurrentAuthToken(localStorage.getItem("myToken"))
        :
        setAuthToken(null)
    }, [])


const handleLogOut = (event) => {
    event.preventDefault()
localStorage.removeItem("myToken")
setCurrentAuthToken("")
setAuthToken(null)
}    


const handleSubmit = async (event) =>{
    let loginInfo = {
        user: {
            username: `${username}`,
            password: `${password}`
        }
        }
event.preventDefault()
await loginUser(loginInfo).then((result) => {
    setLoginMessage(result);
    console.log(result)
})
setPassword("")
setUsername("")
setAuthToken(true)
setCurrentAuthToken(localStorage.getItem("myToken"))
}

const [username, setUsername] = useState("")
const [password, setPassword] = useState("")
const handleUsernameChange = (event) =>{
setUsername(event.target.value)
}
const handlePasswordChange = (event) =>{
setPassword(event.target.value)
}



    return (
        authToken ?
        <div id="loggedIn">
        <p>im logged in</p>
        <p>{loginMessage? loginMessage.data : ""}</p>
        <button type="submit" onClick={handleLogOut}>Log Out</button>
        </div>
        :
        <div id ="login">
            <form onSubmit={handleSubmit}>
                <label htmlFor="username">Username:</label>
                <input type="text"  name="username" value={username}  onChange={handleUsernameChange}/>
                <label htmlFor="password">Password:</label>
                <input type="text" name="password" value={password} onChange={handlePasswordChange}/>
                <button type="submit">Submit</button>
                <Link to="/register">Need to register?</Link>
            </form>
        </div>

    )
}


export default LoginForm
