import React, { useEffect, useState } from 'react';
import { CreateMessage, CreatePost } from '.';
import { getPosts } from '../api';
export const cohortName = '2110-VPI-WEB-PT';
export const API = `https://strangers-things.herokuapp.com/api/${cohortName}`;
import DeletePost from './DeletePost';
import  EditPost  from './EditPost';


const Posts= (props) => {

  const [loadedPosts, setLoadedPosts] = useState([]);



    useEffect(() => {
    getPosts().then(result=>{
      setLoadedPosts(result.data.posts)
      console.log(result)
    }).catch(error=>{
      console.error(error)
    })}, []); 



    return (
        <div id= "postspage">

           <div id ="postsandcreate">
           <CreatePost setLoadedPosts={setLoadedPosts} loadedPosts={loadedPosts}/>
           <div id = "posts">
           <h1>Posts</h1>
          {loadedPosts.map(item => 
          <div key={item._id} className='indvPosts'>
          <p>{item.title}</p>
          <p>{item.author.username}</p>
          <p>{item.createdAt}</p>
          <p>{item.price}</p>
          <p>{item.description}</p>
          <p>{item.location}</p>
          <p>{item.willDeliver}</p>
          {item.isAuthor ? <p>Messages:{item.messages}</p>: <p></p>}
          {item.isAuthor ? <DeletePost postId={item._id} setLoadedPosts={setLoadedPosts} loadedPosts={loadedPosts}/>: <p></p>}
          {item.isAuthor ? <EditPost />: <p></p>}
          {(() => {
        if (localStorage.getItem("myToken")&& !(item.isAuthor)) {
          return (
           <CreateMessage />
          )
        } else if (item.isAuthor) {
          return (
            <div>author true</div>
          )
        } else {
          return (
            <div>catch all</div>
          )
        }
      })()}    


          {(() => {if (localStorage.getItem("myToken")){
     return (<CreateMessage />)
    }else 
    return (<p>TEST</p>)})}

          <p>---------</p>
          </div>
          )}
           </div>
           </div>
        </div>


    )

}

export default Posts
