import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import {BrowserRouter as Router, Route, Link, useHistory} from 'react-router-dom'
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { theme} from "./Theme"
import { makeStyles } from "@mui/styles";
import Button from '@mui/material/Button';
import ButtonGroup from '@mui/material/ButtonGroup';
import Box from '@mui/material/Box';
import AccountBoxIcon from '@mui/icons-material/AccountBox';
import LogoutIcon from '@mui/icons-material/Logout';
import LoginIcon from '@mui/icons-material/Login';
import IconButton from '@mui/material/IconButton';
import Stack from '@mui/material/Stack';
import DeleteIcon from '@mui/icons-material/Delete';
import ForumIcon from '@mui/icons-material/Forum';

import {
    LoginForm, 
    CreatePost, 
    Posts, 
    Profile, 
    Register,
    CreateMessage,
    ViewPost,
    IndividualPost,
    EditPost
} from "./components";


const App = () => {
  const [loadedPosts, setLoadedPosts] = useState([]);
  const [postsToDisplay, setPostsToDisplay] = useState([])
  const [authToken, setAuthToken] = useState(true)
  const [currentAuthToken, setCurrentAuthToken] = useState("")
//   const history = useHistory()
//   <Route path ="/red" component ={red}
// button onclick={() => history.pushState("/red")}
useEffect(() => {
  localStorage.getItem("myToken")
  ?
  setCurrentAuthToken(localStorage.getItem("myToken"))
  :
  setAuthToken(null)
}, [])

    return(
        <Router>
              <ThemeProvider theme={theme}>
        <div className='app'>
        <div id="float"className="hidden" >
             <p></p>
           </div>
            <div id='navigation'>
            <Box
      sx={{m: 1, ml:0 }}>
      <ButtonGroup color="secondary" size="large" variant="contained" aria-label="text button group">
      <Link to="/posts" id="postslink"> <IconButton><ForumIcon /></IconButton></Link>
      {authToken ? <Link to="/profile" id="profilelink"> <IconButton aria-label="profile"><AccountBoxIcon /></IconButton></Link> : <></>} 
      <Link to="/login" id="loggedinlink"> {authToken ? <IconButton aria-label="Log in"><LoginIcon /> </IconButton>: <IconButton aria-label="Log Out"><LogoutIcon /> </IconButton>} 
      </Link>

      </ButtonGroup>
    </Box>
    <h1 id="titlelink">Strangers Things</h1>
            {/* <Link to="/posts" id="postslink">Posts</Link>
           {authToken ?<Link to="/profile" id="profilelink">Profile</Link> : <></>} 
            <Link to="/login" id="loggedinlink">{authToken ? "Log Out" : "Log In"}</Link>
           <h1 id="titlelink">Strangers Things</h1> */}
            </div>
<div id='main-section'>
<Route exact path={["/", "/login"]}>
         <LoginForm authToken={authToken} setAuthToken={setAuthToken} currentAuthToken={currentAuthToken} setCurrentAuthToken={setCurrentAuthToken}/>
         </Route>

       <Route exact path="/posts">
         <Posts loadedPosts={loadedPosts} setLoadedPosts={setLoadedPosts} postsToDisplay={postsToDisplay} setPostsToDisplay={setPostsToDisplay}/>
         </Route>

         <Route path="/profile">
           <Profile />
         </Route>
         <Route path="/register">
         <Register />
         </Route>
         <Route path="/posts/:id">
         <ViewPost loadedPosts={loadedPosts} setLoadedPosts={setLoadedPosts} setPostsToDisplay={setPostsToDisplay}/>
         </Route>
         <Route path="/editpost">
         <EditPost />
         </Route>
      </div>
</div>
</ThemeProvider>
</Router>
    )
}

ReactDOM.render(
    <App />,
    document.querySelector("#app")
  )
