import React, { useEffect, useState } from "react";
import { callApi, getMe } from "../api";
import Alert from '@mui/material/Alert';
import Stack from '@mui/material/Stack';


const Profile = (props) => {
const [myProfile, setMyProfile] = useState([])

    useEffect(() => {
        callApi({ url: "/users/me", token:localStorage.getItem("myToken")}).then(result=>{
          setMyProfile(result.data)
          console.log(result.data)
        }).catch(error=>{
          console.error(error)
        })}, []); 
    return (
        <div id="profile">
        <h1>Profile</h1>
        <Stack sx={{ width: '100%' }} spacing={2}>
      <Alert severity="error">This is an error alert — check it out!</Alert>
      <Alert severity="warning">This is a warning alert — check it out!</Alert>
      <Alert severity="info">This is an info alert — check it out!</Alert>
      <Alert severity="success">This is a success alert — check it out!</Alert>
    </Stack>

        <div>
        <h3>My Posts</h3>
        {myProfile.posts ? myProfile.posts.map(post =>
        post.active ?
        <div className="profileposts" key ={"post" + post._id}>
             <p>Post Title:{post.title}</p>
             <p>Post Description:{post.description}</p>
             {post.messages.map((messages,index) =>
             <div className="profilemessages" key={index + 1}>
               <p>Message {index + "a"}</p>
              <p>From:{messages.fromUser.username}</p>
              <p>Message:{messages.content}</p> 
              </div>)}
            </div>:
          <React.Fragment key={post._id + .5}></React.Fragment>):
        <p>no posts</p>}
        {/* {myProfile.messages ? myProfile.messages.map(messages => 
        <div key ={"message" + messages.post._id}>
             <p>From:{messages.fromUser.username}</p>
             <p>Post Title:{messages.post.title}</p>
             <p>Message:{messages.content}</p>
            <p>--------</p>
            </div>):
        <p>no messages</p>} */}
        </div>
        </div>
    )
}


export default Profile
