import { useState } from "react"
import { Link } from "react-router-dom"
import { useParams } from "react-router-dom"
import DeletePost from './DeletePost';
import { EditPost } from ".";

const ViewPost= (props) =>{
    const [indPost, setIndPost] = useState([])
    const { id } = useParams()
    const setPostsToDisplay = props.setPostsToDisplay
    const loadedPosts = props.loadedPosts
    const setLoadedPosts = props.setLoadedPosts
    function postView(post, term){
        return post._id.includes(term)
    }
    const filtIndPost = loadedPosts.filter(post => postView(post, id))
    setIndPost(filtIndPost)
    console.log(loadedPosts)
    return(
        indPost.length ? <div key={indPost[0]._id} className='indvPosts'>
        <p>{indPost[0].title}</p>
        <p>{indPost[0].author.username}</p>
        <p>{indPost[0].createdAt}</p>
        <p>{indPost[0].price}</p>
        <p>{indPost[0].description}</p>
        <p>{indPost[0].location}</p>
        <p>{indPost[0].willDeliver}</p>
        {(() => {
              if (indPost[0].isAuthor && (indPost[0].messages)){
                return (
                    indPost[0].messages.map( (messages, index) => <div key={"message" + messages.post._id + index}>
                    <p>Messages:{messages.content}</p>
                  </div>)
                )
              } else {
                return (
                  <></>
                )
              }
            })()}
            {indPost[0].isAuthor ? <DeletePost postId={indPost[0]._id} setLoadedPosts={setLoadedPosts} 
          loadedPosts={loadedPosts} setPostsToDisplay={setPostsToDisplay}/>: <></>} 
             {indPost[0].isAuthor ? <EditPost id={id} indPost={indPost}/>: <p></p>}
            <Link to="/posts"> <button >All Posts</button> </Link>
        </div>:
        <div>
        <h2>Post Does Not Exist</h2>
        <Link to="/posts"> <button >View</button> </Link>
        </div>
    )
}

export default ViewPost
