import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import {BrowserRouter as Router, Route, Link, useHistory} from 'react-router-dom'

import {
    LoginForm, 
    CreatePost, 
    Posts, 
    Profile, 
    Register,
    CreateMessage,
    ViewPost,
    IndividualPost,
    EditPost
} from "./components";


const App = () => {
  const [loadedPosts, setLoadedPosts] = useState([]);
  const [postsToDisplay, setPostsToDisplay] = useState([])
  const [authToken, setAuthToken] = useState(true)
  const [currentAuthToken, setCurrentAuthToken] = useState("")
//   const history = useHistory()
//   <Route path ="/red" component ={red}
// button onclick={() => history.pushState("/red")}

    return(
        <Router>
        <div className='app'>
        <div id="float"className="hidden" >
             <p>Confirmed</p>
           </div>
            <div id='navigation'>
            <Link to="/">Stranger Things</Link>
            <Link to="/posts">Posts</Link>
           {authToken ?<Link to="/profile">Profile</Link> : <></>} 
            <Link to="/login">{authToken ? "Log Out" : "Log In"}</Link>
            </div>
<div id='main-section'>

<Route exact path={["/", "/login"]}>
         <LoginForm authToken={authToken} setAuthToken={setAuthToken} currentAuthToken={currentAuthToken} setCurrentAuthToken={setCurrentAuthToken}/>
         </Route>

       <Route exact path="/posts">
         <Posts loadedPosts={loadedPosts} setLoadedPosts={setLoadedPosts} postsToDisplay={postsToDisplay} setPostsToDisplay={setPostsToDisplay}/>
         </Route>

         <Route path="/profile">
           <Profile />
         </Route>
         <Route path="/register">
         <Register />
         </Route>
         <Route path="/posts/:id">
         <ViewPost loadedPosts={loadedPosts} setLoadedPosts={setLoadedPosts} setPostsToDisplay={setPostsToDisplay}/>
         </Route>
         <Route path="/editpost">
         <EditPost />
         </Route>
      </div>
</div>
</Router>
    )
}

ReactDOM.render(
    <App />,
    document.getElementById('app')
  )
