import { useState } from "react"
import { editPosts } from "../api"

const EditPost = (props) =>{
    const [editClicked, setEditClicked] = useState(false)
    const id = props.id
    const indPost = props.indPost
    const [description, setDescription] = useState("")
    const [location, setLocation] = useState("")
    const [price, setPrice] = useState("")
    const [title, setTitle] = useState("")
    const [willDeliver, setWillDeliver] = useState(false)
  const clickButton = (event)=>{
      setDescription(indPost[0].description)
      setLocation(indPost[0].description)
      setPrice(indPost[0].description)
      setTitle(indPost[0].description)
      setWillDeliver(indPost[0].description)
      setEditClicked(true)
  }

    const handleSubmitEdit = async (event) =>{
        event.preventDefault()
    let editedInfo= {
        post:{
        title: `${title}` ,
        description: `${description}`,
        price: `${price}`,
        location: `${location}`,
        willDeliver: `${willDeliver}`
        }
    }
    const results = await editPosts(editedInfo, id)
    console.log(editedInfo, id)
    // console.log(results)
}
    const handleDescriptionChange = (event)=>{
        setDescription(event.target.value)
    }
    const handleLocationChange = (event)=>{
        setLocation(event.target.value)
    }
    const handlePriceChange = (event)=>{
        setPrice(event.target.value)
    }
    const handleTitleChange = (event)=>{
        setTitle(event.target.value)
    }
    const handleWillDeliverChange = (event)=>{
        setWillDeliver(event.target.checked)
    }

    return (
        editClicked ?
        <div id = "editpost">
        <form onSubmit={handleSubmitEdit}>
            <label htmlFor="description">Description:</label>
            <input type="text" name="description" value = {description} onChange={handleDescriptionChange}/>
             <label htmlFor="location">Location:</label>
             <input type="text" name="location" value = {location} onChange={handleLocationChange}/>
             <label htmlFor="price">Price:</label>
             <input type="text" name="price" value = {price} onChange={handlePriceChange}/>
             <label htmlFor="title">Title:</label>
             <input type="text" name="title" value = {title} onChange={handleTitleChange}/>
             <label htmlFor="willdeliver">Will Deliver</label>
             <input type="checkbox" name="willdeliver" value = {willDeliver} onChange={handleWillDeliverChange}/>
             <button type="submit">Submit Post</button>
        </form>
        </div>:
        <div>
        <button onClick={clickButton}>Click me pls</button>
        </div>

    )
}




export default EditPost
