import React, { useEffect, useState } from 'react';
import { CreateMessage, CreatePost } from '.';
import { getPosts } from '../api';
export const cohortName = '2110-VPI-WEB-PT';
export const API = `https://strangers-things.herokuapp.com/api/${cohortName}`;
import DeletePost from './DeletePost';
import  EditPost  from './EditPost';
import Search from './Search';

const Posts= (props) => {

  const [loadedPosts, setLoadedPosts] = useState([]);
 const [postsToDisplay, setPostsToDisplay] = useState([])


    useEffect(() => {
    getPosts().then(result=>{
      setLoadedPosts(result.data.posts)
      setPostsToDisplay(result.data.posts)
      console.log(result)
    }).catch(error=>{
      console.error(error)
    })}, []); 



    return (
        <div id= "postspage">
           <div id ="postsandcreate">
           <CreatePost setLoadedPosts={setLoadedPosts} loadedPosts={loadedPosts}/>

           <div id = "posts">
           <h1>Posts</h1>
           <Search loadedPosts={loadedPosts} setPostsToDisplay={setPostsToDisplay}/>
          {postsToDisplay.map(item => 
          <div key={item._id} className='indvPosts'>
          <p>{item.title}</p>
          <p>{item.author.username}</p>
          <p>{item.createdAt}</p>
          <p>{item.price}</p>
          <p>{item.description}</p>
          <p>{item.location}</p>
          <p>{item.willDeliver}</p>
            {(() => {
              if (item.isAuthor && (item.messages)){
                return (
                  item.messages.map( (messages, index) => <div key={"message" + messages.post._id + index}>
                    <p>Messages:{messages.content}</p>
                  </div>)
                )
              } else {
                return (
                  <></>
                )
              }
            })()} 

          {item.isAuthor ? <DeletePost postId={item._id} setLoadedPosts={setLoadedPosts} 
          loadedPosts={loadedPosts}/>: <p></p>}

          {/* {item.isAuthor ? <EditPost />: <p></p>} */}

          {(() => {
        if (localStorage.getItem("myToken")&& !(item.isAuthor)) {
          return (
           <CreateMessage postId={item._id} />
          )
        } else {
          return (
            <></>
          )
        }
      })()}    

          <p>---------</p>
          </div>
          )}
           </div>
           </div>
        </div>


    )

}

export default Posts
