import { useEffect, useState } from "react";
import { getMe } from "../api";

const Profile = (props) => {
const [myProfile, setMyProfile] = useState([])

    useEffect(() => {
        getMe().then(result=>{
          setMyProfile(result.data)
          console.log(result.data)
        }).catch(error=>{
          console.error(error)
        })}, []); 
    return (
        <div>
        <h1>Profile</h1>
        <div>
        <h3>My Posts</h3>
        {myProfile.posts ? myProfile.posts.map(post =>
        post.active ?
        <div key ={"post" + post._id}>
             <p>Post Title:{post.title}</p>
             <p>Post Description:{post.description}</p>
             {post.messages.map((messages,index) =>
             <div>
               <p>Message {index + 1}</p>
              <p>From:{messages.fromUser.username}</p>
              <p>Message:{messages.content}</p> 
              <p>--------</p>
              </div>)}
            <p>--------</p>
            </div>:
            <></>):
        <p>no posts</p>}
        {/* {myProfile.messages ? myProfile.messages.map(messages => 
        <div key ={"message" + messages.post._id}>
             <p>From:{messages.fromUser.username}</p>
             <p>Post Title:{messages.post.title}</p>
             <p>Message:{messages.content}</p>
            <p>--------</p>
            </div>):
        <p>no messages</p>} */}
        </div>
        </div>
    )
}


export default Profile
